mapper.select:
SELECT IFNULL(sum(timediff),0) FROM znzz_waiting_record WHERE
DATE(start_time) =#{map.date}
DATE(start_time) =CURRENT_DATE
AND machine_id =#{map.machineId}

SELECT machine_name as sbmc, maintenance_signal as whxh, partsCount_target as jgmbz, partsCount_result as jgssz, override_rapid as kjbl, override_spindle as zzbl, override_feed as qxbl, spindle_mode as zzms, start_time as kssj, end_time as jssj, timediff as timediff FROM znzz_waiting_record where id > 0
AND DATE(start_time) BETWEEN #{filter.startTime} AND #{filter.endTime}
AND machine_name=#{filter.machineName}
AND timediff BETWEEN 0 AND #{filter.timediff}

SELECT
	IFNULL(sum(timediff),0)
FROM znzz_waiting_record
WHERE  id > 0

    AND DATE(start_time) BETWEEN #{filter.startTime} AND #{filter.endTime}


	AND machine_name=#{filter.machineName}


	AND timediff BETWEEN 0 AND #{filter.timediff}

mapper.delete:
	DELETE FROM 
	 	znzz_waiting_record 
 	WHERE 
 		machine_id=#{machineId}
 	AND  
 		DATE_FORMAT(start_time,'%Y-%m-%d %H:%i')=DATE_FORMAT(#{startTime},'%Y-%m-%d %H:%i');
